Here's an implementation of Dijkstra's algorithm in C++:

```cpp
#include <limits.h>
#include <vector>
#include <queue>
#include <list>

using namespace std;

// Number of vertices in the graph
#define V 9

int minDistance(vector<int> dist, vector<bool> sptSet) {
    int min = INT_MAX, min_index;

    for (int v = 0; v < V; v++)
        if (sptSet[v] == false && dist[v] <= min)
            min = dist[v], min_index = v;

    return min_index;
}

void printPath(vector<int> parent, int j) {
    if (parent[j] == - 1)
        return;
    printPath(parent, parent[j]);
    cout << j << " ";
}

void printSolution(vector<int> dist, vector<int> parent) {
    int src = 0;
    cout << "Vertex \t Distance \t Path";
    for (int i = 1; i < V; i++) {
        cout << "\n" << src << " -> " << i << " \t\t " << dist[i] << "\t\t" << src << " ";
        printPath(parent, i);
    }
}

void dijkstra(vector<vector<int>> graph, int src) {
    vector<int> dist(V);
    vector<bool> sptSet(V);
    vector<int> parent(V, -1);

    for (int i = 0; i < V; i++)
        dist[i] = INT_MAX;

    dist[src] = 0;

    for (int count = 0; count < V - 1; count++) {
        int u = minDistance(dist, sptSet);

        sptSet[u] = true;

        for (int v = 0; v < V; v++)

            if (!sptSet[v] && graph[u][v] && dist[u] != INT_MAX
                && dist[u] + graph[u][v] < dist[v]) {
                parent[v] = u;
                dist[v] = dist[u] + graph[u][v];
            }
    }

    printSolution(dist, parent);
}

int main() {
    vector<vector<int>> graph = { { 0, 4, 0, 0, 0, 0, 0, 8, 0 },
                                  { 4, 0, 8, 0, 0, 0, 0, 11, 0 },
                                  { 0, 8, 0, 7, 0, 4, 0, 0, 2 },
                                  { 0, 0, 7, 0, 9, 14, 0, 0, 0 },
                                  { 0, 0, 0, 9, 0, 10, 0, 0, 0 },
                                  { 0, 0, 4, 14, 10, 0, 2, 0, 0 },
                                  { 0, 0, 0, 0, 0, 2, 0, 1, 6 },
                                  { 8, 11, 0, 0, 0, 0, 1, 0, 7 },
                                  { 0, 0, 2, 0, 0, 0, 6, 7, 0 } };

    dijkstra(graph, 0);

    return 0;
}
```

