Sure, here's an implementation of Kruskal's algorithm in C++. This program constructs a Minimum Spanning Tree (MST) of a given graph using Kruskal's algorithm.

```cpp
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Edge {
    int source, destination, weight;
};

bool compare(Edge a, Edge b) {
    return a.weight < b.weight;
}

int find(int v, vector<int> &parent) {
    if (parent[v] == v)
        return v;
    return parent[v] = find(parent[v], parent);
}

void union_set(int a, int b, vector<int> &parent, vector<int> &rank) {
    a = find(a, parent);
    b = find(b, parent);

    if (rank[a] < rank[b])
        swap(a, b);

    parent[b] = a;

    if (rank[a] == rank[b])
        rank[a]++;
}

vector<Edge> Kruskal(vector<Edge> graph, int V, int E) {
    vector<Edge> mst;
    vector<int> parent(V, 0);
    vector<int> rank(V, 0);

    for (int i = 0; i < V; i++)
        parent[i] = i;

    sort(graph.begin(), graph.end(), compare);

    for (auto it : graph) {
        if (find(it.source, parent) != find(it.destination, parent)) {
            mst.push_back(it);
            union_set(it.source, it.destination, parent, rank);
        }
    }

    return mst;
}

int main() {
    int V = 4;
    int E = 5;
    vector<Edge> graph = { { 0, 1, 10 }, { 1, 2, 2 }, { 2, 0, 4 }, { 1, 3, 6 }, { 2, 3, 7 } };

    vector<Edge> mst = Kruskal(graph, V, E);

    cout << "Edges in the constructed MST are: \n";
    for (auto it : mst)
        cout << it.source << " - " << it.destination << " : " << it.weight << endl;

    return 0;
}
```

