Here's an implementation of a min heap in C++ using the STL vector:

```cpp
#include <vector>
#include <algorithm>
#include <iostream>

class MinHeap {
private:
    std::vector<int> heap;

    void siftUp(int index) {
        while (index > 0 && heap[parent(index)] > heap[index]) {
            std::swap(heap[parent(index)], heap[index]);
            index = parent(index);
        }
    }

    int parent(int index) {
        return (index - 1) / 2;
    }

    void siftDown(int index) {
        int minIndex = index;
        int leftChildIndex = 2 * index + 1;

        if (leftChildIndex < heap.size() && heap[leftChildIndex] < heap[minIndex]) {
            minIndex = leftChildIndex;
        }

        int rightChildIndex = 2 * index + 2;

        if (rightChildIndex < heap.size() && heap[rightChildIndex] < heap[minIndex]) {
            minIndex = rightChildIndex;
        }

        if (minIndex != index) {
            std::swap(heap[index], heap[minIndex]);
            siftDown(minIndex);
        }
    }

public:
    MinHeap() {}

    void insert(int value) {
        heap.push_back(value);
        siftUp(heap.size() - 1);
    }

    int getMin() {
        if (heap.empty()) {
            std::cerr << "Error: The min-heap is empty." << std::endl;
            return -1;
        }

        int minValue = heap.front();
        heap.front() = heap.back();
        heap.pop_back();
        siftDown(0);

        return minValue;
    }

    bool isEmpty() {
        return heap.empty();
    }

    void printHeap() {
        for (int i = 0; i < heap.size(); ++i) {
            std::cout << heap[i] << " ";
        }
        std::cout << std::endl;
    }
};

int main() {
    MinHeap minHeap;

    minHeap.insert(5);
    minHeap.insert(3);
    minHeap.insert(8);
    minHeap.insert(1);

    minHeap.printHeap();

    std::cout << "Minimum value: " << minHeap.getMin() << std::endl;

    return 0;
}
```

