vector<Edge> kruskal(int n, vector<Edge> const &edges) {
    DisjointSetUnion dsu(n);
    vector<Edge> result;

    // Sort edges by weight
    vector<Edge> edge_list(edges.begin(), edges.end());
    sort(edge_list.begin(), edge_list.end(), compare_weight);

    // Process edges in order of weight
    for (auto const &e : edge_list) {
        int root1 = dsu.find_set(e.from);
        int root2 = dsu.find_set(e.to);

        if (root1 != root2) {
            // Add edge to result and update the DSU
            result.push_back(e);
            dsu.union_sets(root1, root2);
        }
    }

    return result;
}
