```cpp
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Edge {
    int src, dest, weight;
};

struct DisjointSet {
    int parent, rank;
};

int find(DisjointSet set[], int i) {
    if (set[i].parent != i)
        set[i].parent = find(set, set[i].parent);

    return set[i].parent;
}

void unionSet(DisjointSet set[], int x, int y) {
    int xroot = find(set, x);
    int yroot = find(set, y);

    if (set[xroot].rank < set[yroot].rank)
        set[xroot].parent = yroot;
    else if (set[xroot].rank > set[yroot].rank)
        set[yroot].parent = xroot;
    else {
        set[yroot].parent = xroot;
        set[xroot].rank++;
    }
}

bool compare(Edge a, Edge b) {
    return a.weight < b.weight;
}

void kruskal(Edge edges[], int n, int m) {
    DisjointSet set[n];
    for (int i = 0; i < n; i++) {
        set[i].parent = i;
        set[i].rank = 1;
    }

    sort(edges, edges + m, compare);

    int count = 0, i = 0;
    vector<Edge> result;
    while (count < n - 1) {
        Edge currentEdge = edges[i];

        int xset = find(set, currentEdge.src);
        int yset = find(set, currentEdge.dest);

        if (xset != yset) {
            result.push_back(currentEdge);
            count++;
            unionSet(set, xset, yset);
        }

        i++;
    }

    cout << "The edges in the MST are:\n";
    for (auto x: result)
        cout << x.src << " -- " << x.dest << " == " << x.weight << "\n";
}

int main() {
    int n, m;
    cout << "Enter the number of vertices and edges:\n";
    cin >> n >> m;

    Edge edges[m];
    cout << "Enter the edges with their source, destination and weight:\n";
    for (int i = 0; i < m; i++) {
        cin >> edges[i].src >> edges[i].dest >> edges[i].weight;
    }

    kruskal(edges, n, m);

    return 0;
}
```

