```cpp
#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

struct Edge {
    int from, to, weight;

    bool operator<(const Edge& other) const {
        return weight < other.weight;
    }
};

int find(vector<int>& parent, int x) {
    if (parent[x] != x) {
        parent[x] = find(parent, parent[x]);
    }
    return parent[x];
}

void union_set(vector<int>& parent, vector<int>& rank, int x, int y) {
    int rootX = find(parent, x);
    int rootY = find(parent, y);

    if (rank[rootX] > rank[rootY]) {
        parent[rootY] = rootX;
    } else if (rank[rootX] < rank[rootY]) {
        parent[rootX] = rootY;
    } else {
        parent[rootY] = rootX;
        rank[rootX]++;
    }
}

vector<Edge> kruskal(vector<vector<Edge>>& graph) {
    int n = graph.size();
    vector<Edge> mst;
    vector<int> parent(n), rank(n);

    iota(begin(parent), end(parent), 0);
    sort(begin(graph), end(graph));

    for (const Edge& e : graph) {
        int x = find(parent, e.from);
        int y = find(parent, e.to);

        if (x != y) {
            mst.push_back(e);
            union_set(parent, rank, x, y);
        }
    }

    return mst;
}

int main() {
    int n, m;
    cin >> n >> m;

    vector<vector<Edge>> graph(n);
    for (int i = 0; i < m; ++i) {
        int a, b, w;
        cin >> a >> b >> w;
        graph[a].push_back({a, b, w});
        graph[b].push_back({b, a, w});
    }

    vector<Edge> mst = kruskal(graph);
    int totalWeight = accumulate(begin(mst), end(mst), 0, [](int sum, const Edge& e) {
        return sum + e.weight;
    });

    cout << "Total weight of MST: " << totalWeight << "\n";
    for (const Edge& e : mst) {
        cout << e.from << " - " << e.to << "\n";
    }

    return 0;
}
