```cpp
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Edge {
    int src, dest, weight;
};

struct DisjointSet {
    int parent, rank;
};

int find(DisjointSet set[], int i) {
    if (set[i].parent != i)
        set[i].parent = find(set, set[i].parent);

    return set[i].parent;
}

void unionSet(DisjointSet set[], int x, int y) {
    int xroot = find(set, x);
    int yroot = find(set, y);

    if (set[xroot].rank < set[yroot].rank)
        set[xroot].parent = yroot;
    else if (set[xroot].rank > set[yroot].rank)
        set[yroot].parent = xroot;
    else {
        set[yroot].parent = xroot;
        set[xroot].rank++;
    }
}

bool compare(Edge a, Edge b) {
    return a.weight < b.weight;
}

void KruskalMST(Edge edges[], int n, int m) {
    DisjointSet set[n];

    for (int i = 0; i < n; i++) {
        set[i].parent = i;
        set[i].rank = 1;
    }

    vector<Edge> mst;

    sort(edges, edges + m, compare);

    for (int i = 0; i < m; i++) {
        int x = find(set, edges[i].src);
        int y = find(set, edges[i].dest);

        if (x != y) {
            mst.push_back(edges[i]);
            unionSet(set, x, y);
        }
    }

    cout << "Edges in the minimum spanning tree are:\n";
    for (auto it = mst.begin(); it != mst.end(); it++)
        cout << it->src << " -- " << it->dest << " == " << it->weight << endl;
}

int main() {
    int n, m;
    cout << "Enter number of vertices and edges: ";
    cin >> n >> m;

    Edge edges[m];

    cout << "Enter edges with their source, destination and weight:\n";
    for (int i = 0; i < m; i++) {
        cin >> edges[i].src >> edges[i].dest >> edges[i].weight;
    }

    KruskalMST(edges, n, m);

    return 0;
}
