Sure, here is an implementation of Kruskal's algorithm in C++. This program will take as input a list of edges with their weights and will output the minimum spanning tree (MST) using Kruskal's algorithm.

```cpp
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Edge {
    int from, to, weight;
};

bool compare(Edge a, Edge b) {
    return a.weight < b.weight;
}

int find(vector<int>& parent, int x) {
    if (parent[x] == x)
        return x;
    return parent[x] = find(parent, parent[x]);
}

void union_set(vector<int>& parent, vector<int>& rank, int x, int y) {
    x = find(parent, x);
    y = find(parent, y);

    if (rank[x] > rank[y])
        parent[y] = x;
    else if (rank[x] < rank[y])
        parent[x] = y;
    else {
        parent[y] = x;
        rank[x]++;
    }
}

vector<Edge> kruskal(vector<Edge>& edges, int n) {
    sort(edges.begin(), edges.end(), compare);

    vector<int> parent(n), rank(n);
    for (int i = 0; i < n; i++) {
        parent[i] = i;
        rank[i] = 1;
    }

    vector<Edge> mst;
    for (Edge e : edges) {
        if (find(parent, e.from) != find(parent, e.to)) {
            mst.push_back(e);
            union_set(parent, rank, e.from, e.to);
        }
    }

    return mst;
}

int main() {
    int n = 5; // number of vertices
    vector<Edge> edges = { {0, 1, 2}, {0, 2, 5}, {0, 3, 8}, {1, 2, 3}, {1, 3, 6}, {2, 4, 7}, {3, 4, 9} };

    vector<Edge> mst = kruskal(edges, n);

    cout << "Minimum Spanning Tree:" << endl;
    for (Edge e : mst)
        cout << e.from << " - " << e.to << " : " << e.weight << endl;

    return 0;
}
```

