```c
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Edge {
    int from, to, weight;
    Edge(int f, int t, int w) : from(f), to(t), weight(w) {}
};

class UnionFind {
public:
    UnionFind(int n) : parent(n), rank(n, 1) {
        for (int i = 0; i < n; i++) {
            parent[i] = i;
        }
    }

    int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    void merge(int x, int y) {
        int rootX = find(x);
        int rootY = find(y);
        if (rootX == rootY) return;

        if (rank[rootX] < rank[rootY]) {
            parent[rootX] = rootY;
            rank[rootY] += rank[rootX];
        } else {
            parent[rootY] = rootX;
            rank[rootX] += rank[rootY];
        }
    }

private:
    vector<int> parent;
    vector<int> rank;
};

int kruskal(vector<vector<int>>& graph, int n) {
    vector<Edge> edges;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (graph[i][j] != 0) {
                edges.push_back(Edge(i, j, graph[i][j]));
            }
        }
    }

    sort(edges.begin(), edges.end(), [](const Edge& a, const Edge& b) {
        return a.weight < b.weight;
    });

    UnionFind uf(n);
    int mst_weight = 0;
    for (const Edge& e : edges) {
        if (uf.find(e.from) != uf.find(e.to)) {
            uf.merge(e.from, e.to);
            mst_weight += e.weight;
        }
    }

    return mst_weight;
}

int main() {
    int n = 5;
    vector<vector<int>> graph = {
        {0, 10, 20, 0, 0},
        {10, 0, 0, 50, 10},
        {20, 0, 0, 33, 20},
        {0, 50, 33, 0, 10},
        {0, 10, 20, 10, 0}
    };

    cout << "Minimum spanning tree weight: " << kruskal(graph, n) << endl;

    return 0;
}
```
