```c
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

class Edge {
public:
    int src, dest, weight;

    Edge(int src, int dest, int weight) {
        this->src = src;
        this->dest = dest;
        this->weight = weight;
    }

    bool operator<(const Edge& other) const {
        return weight < other.weight;
    }
};

class UnionFind {
private:
    vector<int> parent;
    vector<int> rank;

public:
    UnionFind(int n) {
        parent.resize(n);
        rank.resize(n);

        for (int i = 0; i < n; i++) {
            parent[i] = i;
            rank[i] = 1;
        }
    }

    int find(int node) {
        if (parent[node] != node) {
            parent[node] = find(parent[node]);
        }

        return parent[node];
    }

    void unionSets(int node1, int node2) {
        int root1 = find(node1);
        int root2 = find(node2);

        if (root1 != root2) {
            if (rank[root1] < rank[root2]) {
                parent[root1] = root2;
                rank[root2] += rank[root1];
            } else {
                parent[root2] = root1;
                rank[root1] += rank[root2];
            }
        }
    }
};

class MinimumSpanningTree {
private:
    vector<Edge> edges;

public:
    MinimumSpanningTree(vector<Edge>& inputEdges, int numVertices) {
        // Sort the edges in ascending order of their weights
        sort(inputEdges.begin(), inputEdges.end());

        UnionFind uf(numVertices);

        for (Edge e : inputEdges) {
            if (uf.find(e.src) != uf.find(e.dest)) {
                edges.push_back(e);
                uf.unionSets(e.src, e.dest);
            }
        }
    }

    void printMST() {
        for (Edge e : edges) {
            cout << "(" << e.src << ", " << e.dest << ") -> " << e.weight << endl;
        }
    }
};

int main() {
