Here is a simple implementation of a min heap in C++ using an array:

```cpp
#include <iostream>
#include <vector>

class MinHeap {
private:
    std::vector<int> heap;

    void siftDown(int index) {
        int smallest = index;
        int leftChild = 2 * index + 1;
        int rightChild = 2 * index + 2;

        if (leftChild < heap.size() && heap[leftChild] < heap[smallest]) {
            smallest = leftChild;
        }

        if (rightChild < heap.size() && heap[rightChild] < heap[smallest]) {
            smallest = rightChild;
        }

        if (smallest != index) {
            std::swap(heap[index], heap[smallest]);
            siftDown(smallest);
        }
    }

public:
    void insert(int value) {
        heap.push_back(value);
        siftUp(heap.size() - 1);
    }

    int getMin() {
        if (heap.empty()) {
            throw std::runtime_error("Cannot get minimum element from an empty heap");
        }
        return heap[0];
    }

    void removeMin() {
        if (heap.size() == 1) {
            heap.pop_back();
            return;
        }

        heap[0] = heap.back();
        heap.pop_back();
        siftDown(0);
    }

    void siftUp(int index) {
        while (index > 0) {
            int parent = (index - 1) / 2;

            if (heap[parent] <= heap[index]) {
                break;
            }

            std::swap(heap[parent], heap[index]);
            index = parent;
        }
    }

    bool isEmpty() {
        return heap.empty();
    }
};

int main() {
    MinHeap minHeap;

    minHeap.insert(5);
    minHeap.insert(3);
    minHeap.insert(8);
    minHeap.insert(1);

    while (!minHeap.isEmpty()) {
        std::cout << minHeap.getMin() << " ";
        minHeap.removeMin();
    }

    return 0;
}
```

