```cpp
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Edge {
    int src, dest, weight;
};

struct DisjointSet {
    int parent, rank;
};

int find(DisjointSet set[], int i) {
    if (set[i].parent != i)
        set[i].parent = find(set, set[i].parent);

    return set[i].parent;
}

void Union(DisjointSet set[], int x, int y) {
    int xroot = find(set, x);
    int yroot = find(set, y);

    if (set[xroot].rank < set[yroot].rank)
        set[xroot].parent = yroot;
    else if (set[xroot].rank > set[yroot].rank)
        set[yroot].parent = xroot;
    else {
        set[yroot].parent = xroot;
        set[xroot].rank++;
    }
}

int myComp(Edge a, Edge b) {
    return a.weight < b.weight;
}

void KruskalMST(Edge edges[], int n, int e) {
    DisjointSet set[n];
    for (int i = 0; i < n; i++) {
        set[i].parent = i;
        set[i].rank = 0;
    }

    vector<Edge> mst;

    sort(edges, edges+e, myComp);

    for (int i=0; i<e; i++) {
        int x = find(set, edges[i].src);
        int y = find(set, edges[i].dest);
        if (x != y) {
            mst.push_back(edges[i]);
            Union(set, x, y);
        }
    }

    cout << "The edges in the MST are:\n";
    for (auto it = mst.begin(); it != mst.end(); it++)
        cout << it->src << " -- " << it->dest << " == " << it->weight << endl;
}

int main() {
    int V = 5;
    Edge edges[] = { { 0, 1, 2 }, { 1, 2, 3 }, { 2, 3, 4 }, { 3, 4, 5 }, { 4, 0, 6 } };
    int E = sizeof(edges)/sizeof(edges[0]);

    KruskalMST(edges, V, E);

    return 0;
}
```

