```cpp
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Edge {
    int u, v, weight;
};

bool compare(Edge a, Edge b) {
    return a.weight < b.weight;
}

int find(vector<int> &parent, int i) {
    if (parent[i] == i)
        return i;
    return parent[i] = find(parent, parent[i]);
}

void union_sets(vector<int> &parent, vector<int> &rank, int x, int y) {
    x = find(parent, x);
    y = find(parent, y);

    if (rank[x] < rank[y])
        parent[x] = y;
    else if (rank[y] < rank[x])
        parent[y] = x;
    else {
        parent[y] = x;
        rank[x]++;
    }
}

void kruskal(vector<vector<int>> &graph, int V) {
    vector<Edge> edges;
    for (int i = 0; i < V; i++) {
        for (int j = 0; j < V; j++) {
            if (graph[i][j] != 0)
                edges.push_back({i, j, graph[i][j]});
        }
    }

    sort(edges.begin(), edges.end(), compare);

    vector<int> parent(V);
    vector<int> rank(V, 0);

    for (int i = 0; i < V; i++)
        parent[i] = i;

    int minCost = 0;
    vector<pair<int, int>> mst;

    for (auto edge : edges) {
        int x = find(parent, edge.u);
        int y = find(parent, edge.v);

        if (x != y) {
            minCost += edge.weight;
            mst.push_back({edge.u, edge.v});
            union_sets(parent, rank, x, y);
        }
    }

    cout << "Minimum spanning tree cost: " << minCost << endl;
    cout << "Edges in MST:" << endl;
    for (auto edge : mst)
        cout << edge.first << " - " << edge.second << endl;
}

