#include <iostream>
#include <vector>
#include <unordered_map>

using namespace std;

struct DisjointSet {
    unordered_map<int, int> parent;
    unordered_map<int, int> rank;

    DisjointSet(int n) {
        for (int i = 0; i < n; i++) {
            parent[i] = i;
            rank[i] = 1;
        }
    }

    int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    void union_set(int x, int y) {
        int root_x = find(x);
        int root_y = find(y);
        if (root_x == root_y) return;
        if (rank[root_x] < rank[root_y]) {
            parent[root_x] = root_y;
            rank[root_y] += rank[root_x];
        } else {
            parent[root_y] = root_x;
            rank[root_x] += rank[root_y];
        }
    }
};

vector<pair<int, int>> kruskal(vector<pair<int, pair<int, int>>>& edges, int n) {
