    DisjointSet ds(n);
    vector<pair<int, int>> mst;
    for (auto& edge : edges) {
        int u = edge.second.first;
        int v = edge.second.second;
        int set_u = ds.find(u);
        int set_v = ds.find(v);
        if (set_u != set_v) {
            mst.push_back(edge.second);
            ds.union_set(set_u, set_v);
        }
    }
    return mst;
}

