#include <vector>
#include <algorithm>
using namespace std;

struct Edge {
    int u, v, weight;
};

bool compare(Edge a, Edge b) {
    return a.weight < b.weight;
}

int find(int v, vector<int> &parent) {
    if (parent[v] == v)
        return v;
    return parent[v] = find(parent[v], parent);
}

void union_set(int u, int v, vector<int> &parent, vector<int> &rank) {
    u = find(u, parent);
    v = find(v, parent);

    if (rank[u] < rank[v])
        swap(u, v);

    if (rank[u] == rank[v])
        rank[u]++;

    parent[v] = u;
}

vector<Edge> kruskal(vector<vector<int>> &graph, int V) {
    int E = V * (V - 1) / 2;
    vector<Edge> result(E);
    vector<int> parent(V), rank(V);

    for (int i = 0; i < V; i++) {
        parent[i] = i;
        rank[i] = 1;
    }

    sort(result.begin(), result.end(), compare);

    int i = 0, e = 0;
    while (e < V - 1) {
        Edge next = result[i++];

        int u = find(next.u, parent);
        int v = find(next.v, parent);

        if (u != v) {
            union_set(u, v, parent, rank);
            result[e++] = next;
        }
    }

    return result;
}

