Here's an implementation of a min heap in C++ using a vector to store the elements:

```cpp
#include <vector>
#include <algorithm>
#include <stdexcept>

class MinHeap {
private:
    std::vector<int> heap;

    void siftDown(int index) {
        int minIndex = index;
        int leftChildIndex = 2 * index + 1;
        int rightChildIndex = 2 * index + 2;

        if (leftChildIndex < heap.size() && heap[leftChildIndex] < heap[minIndex]) {
            minIndex = leftChildIndex;
        }

        if (rightChildIndex < heap.size() && heap[rightChildIndex] < heap[minIndex]) {
            minIndex = rightChildIndex;
        }

        if (minIndex != index) {
            std::swap(heap[index], heap[minIndex]);
            siftDown(minIndex);
        }
    }

public:
    MinHeap() {}

    void insert(int value) {
        heap.push_back(value);
        siftUp(heap.size() - 1);
    }

    int getMin() {
        if (heap.empty()) {
            throw std::runtime_error("Heap is empty");
        }

        int minValue = heap.front();
        heap.front() = heap.back();
        heap.pop_back();
        siftDown(0);

        return minValue;
    }

    bool isEmpty() {
        return heap.empty();
    }

private:
    void siftUp(int index) {
        while (index > 0 && heap[(index - 1) / 2] > heap[index]) {
            std::swap(heap[(index - 1) / 2], heap[index]);
            index = (index - 1) / 2;
        }
    }
};
```

