```c
#include <iostream>
#include <vector>
#include <list>
using namespace std;

struct Edge {
    int u, v, w;
    Edge(int _u, int _v, int _w) : u(_u), v(_v), w(_w) {}
};

struct DisjointSet {
    int parent, rank;
    DisjointSet(int x) : parent(x), rank(0) {}
};

int find(DisjointSet& ds, int u) {
    if (ds.parent != u)
        ds.parent = find(ds, ds.parent);
    return ds.parent;
}

void merge(DisjointSet& ds1, DisjointSet& ds2) {
    if (ds1.rank > ds2.rank)
        ds2.parent = ds1.parent;
    else if (ds1.rank < ds2.rank)
        ds1.parent = ds2.parent;
    else {
        ds2.parent = ds1.parent;
        ds1.rank++;
    }
}

int kruskal(vector<list<Edge>>& graph, int n) {
    vector<DisjointSet> ds(n);
    for (int i = 0; i < n; i++)
        ds[i] = DisjointSet(i);
    vector<Edge> mst;
    for (auto& e : graph) {
        int u = find(ds[e.front().u], e.front().u);
        int v = find(ds[e.front().v], e.front().v);
        if (u != v) {
            mst.push_back(e.front());
            merge(ds[u], ds[v]);
        }
    }
    int weight = 0;
    for (auto& e : mst)
        weight += e.w;
    return weight;
}

int main() {
    int n, m;
    cin >> n >> m;
    vector<list<Edge>> graph(n);
    for (int i = 0; i < m; i++) {
        int u, v, w;
        cin >> u >> v >> w;
        graph[u].push_back(Edge(u, v, w));
        graph[v].push_back(Edge(v, u, w));
    }
    cout << kruskal(graph, n) << endl;
    return 0;
}
```
