```cpp
#include <iostream>
#include <vector>

class MinHeap {
private:
    std::vector<int> heap;

    void siftUp(int index) {
        while (index > 0 && heap[parent(index)] > heap[index]) {
            std::swap(heap[parent(index)], heap[index]);
            index = parent(index);
        }
    }

    void siftDown(int index) {
        int minIndex = index;
        int leftChildIndex = leftChild(index);

        if (leftChildIndex < heap.size() && heap[leftChildIndex] < heap[minIndex]) {
            minIndex = leftChildIndex;
        }

        int rightChildIndex = rightChild(index);

        if (rightChildIndex < heap.size() && heap[rightChildIndex] < heap[minIndex]) {
            minIndex = rightChildIndex;
        }

        if (index != minIndex) {
            std::swap(heap[index], heap[minIndex]);
            siftDown(minIndex);
        }
    }

    int parent(int index) {
        return (index - 1) / 2;
    }

    int leftChild(int index) {
        return 2 * index + 1;
    }

    int rightChild(int index) {
        return 2 * index + 2;
    }

public:
    void insert(int value) {
        heap.push_back(value);
        siftUp(heap.size() - 1);
    }

    int extractMin() {
        if (heap.empty()) {
            throw std::runtime_error("Cannot extract min from an empty heap");
        }

        int result = heap.front();
        heap.front() = heap.back();
        heap.pop_back();
        siftDown(0);

        return result;
    }
};

int main() {
