```c
#include <iostream>
#include <vector>
#include <limits>

using namespace std;

struct Edge {
    int u, v;
    int weight;

    Edge(int u, int v, int weight) : u(u), v(v), weight(weight) {}
};

class DisjointSet {
public:
    DisjointSet(int n) : parent(n), rank(n, 0) {
        for (int i = 0; i < n; i++) {
            parent[i] = i;
        }
    }

    int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    void merge(int x, int y) {
        int rootX = find(x);
        int rootY = find(y);

        if (rootX == rootY) {
            return;
        }

        if (rank[rootX] < rank[rootY]) {
            parent[rootX] = rootY;
        } else if (rank[rootX] > rank[rootY]) {
            parent[rootY] = rootX;
        } else {
            parent[rootY] = rootX;
            rank[rootX]++;
        }
    }

private:
    vector<int> parent;
    vector<int> rank;
};

vector<Edge> kruskal(vector<vector<int>>& graph, int n) {
    vector<Edge> mst;
    vector<Edge> edges = {};

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (graph[i][j] != 0) {
                edges.push_back(Edge(i, j, graph[i][j]));
            }
        }
    }

    sort(edges.begin(), edges.end(), [](const Edge& a, const Edge& b) {
        return a.weight < b.weight;
    });

    DisjointSet ds(n);

    for (const auto& edge : edges) {
        int u = edge.u;
        int v = edge.v;

        if (ds.find(u) != ds.find(v)) {
            mst.push_back(edge);
            ds.merge(u, v);
        }
    }

    return mst;
}

int main() {
