    vector<vector<int>> graph = {
        {0, 10, 20, 0, 0, 0},
        {10, 0, 5, 16, 0, 0},
        {20, 5, 0, 20, 1, 0},
        {0, 16, 20, 0, 2, 6},
        {0, 0, 1, 2, 0, 25},
        {0, 0, 0, 6, 25, 0}
    };

    vector<Edge> mst = kruskal(graph, n);

    int totalWeight = 0;
    for (const auto& edge : mst) {
        totalWeight += edge.weight;
        cout << "(" << edge.u << ", " << edge.v << ") " << edge.weight << endl;
    }

