```cpp
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Edge {
    int src, dest, weight;
};

struct DisjointSet {
    int parent, rank;
};

int find(DisjointSet set[], int i) {
    if (set[i].parent != i)
        set[i].parent = find(set, set[i].parent);

    return set[i].parent;
}

void unionSet(DisjointSet set[], int src, int dest) {
    int srcRoot = find(set, src);
    int destRoot = find(set, dest);

    if (set[srcRoot].rank < set[destRoot].rank)
        set[srcRoot].parent = destRoot;
    else if (set[srcRoot].rank > set[destRoot].rank)
        set[destRoot].parent = srcRoot;
    else {
        set[destRoot].parent = srcRoot;
        set[srcRoot].rank++;
    }
}

int isCycle(DisjointSet set[], int V, int start, int end) {
    if (find(set, start) == find(set, end))
        return 1;

    return 0;
}

void sortEdges(Edge arr[], int n) {
    sort(arr, arr+n, [](Edge a, Edge b) {
        return a.weight < b.weight;
    });
}

void printMST(Edge result[], int V, int E) {
    int sum = 0;
    cout << "Edges in the constructed MST are:\n";
    for (int i = 0; i < E; i++) {
        cout << result[i].src + 1 << " -- " << result[i].dest + 1 << " == " << result[i].weight << "\n";
        sum += result[i].weight;
    }
    cout << "Minimum Cost: " << sum;
}

void KruskalMST(Edge arr[], int V, int E) {
    DisjointSet set[V];
    Edge result[V];

    for (int i = 0; i < V; i++) {
        set[i].parent = i;
        set[i].rank = 0;
    }

    sortEdges(arr, E);

    int e = 0;
    for (int i = 0; i < E && e < V - 1; i++) {
        Edge nextEdge = arr[i];

        if (!isCycle(set, V, nextEdge.src, nextEdge.dest)) {
            result[e++] = nextEdge;
            unionSet(set, nextEdge.src, nextEdge.dest);
        }
    }

    printMST(result, V, e);
}

int main() {
    int V = 4; // Number of vertices in the graph
    int E = 5; // Number of edges in the graph

    Edge arr[E];

    // Add edges to the graph
    arr[0] = {0, 1, 10};
    arr[1] = {1, 2, 20};
    arr[2] = {2, 0, 30};
    arr[3] = {1, 3, 40};
    arr[4] = {3, 2, 50};

    KruskalMST(arr, V, E);

    return 0;
}
```

