Here is a simple implementation of a min heap in C++ using the STL vector:

```cpp
#include <vector>

class MinHeap {
private:
    std::vector<int> heap;

    void siftDown(int index) {
        int minIndex = index;
        int leftChildIndex = 2 * index + 1;

        if (leftChildIndex < heap.size() && heap[leftChildIndex] < heap[minIndex]) {
            minIndex = leftChildIndex;
        }

        int rightChildIndex = 2 * index + 2;

        if (rightChildIndex < heap.size() && heap[rightChildIndex] < heap[minIndex]) {
            minIndex = rightChildIndex;
        }

        if (minIndex != index) {
            std::swap(heap[index], heap[minIndex]);
            siftDown(minIndex);
        }
    }

public:
    // Insert a new element into the min heap
    void insert(int value) {
        heap.push_back(value);
        siftUp(heap.size() - 1);
    }

    // Remove and return the smallest element from the min heap
    int extractMin() {
        if (heap.empty()) {
            throw std::runtime_error("Cannot extract from an empty min heap");
        }

        int result = heap.front();
        heap.front() = heap.back();
        heap.pop_back();
        siftDown(0);

        return result;
    }

    // Get the smallest element from the min heap without removing it
    int getMin() const {
        if (heap.empty()) {
            throw std::runtime_error("Cannot get the minimum from an empty min heap");
        }

        return heap.front();
    }

private:
    void siftUp(int index) {
        while (index > 0) {
            int parentIndex = (index - 1) / 2;

            if (heap[parentIndex] <= heap[index]) {
                break;
            }

            std::swap(heap[parentIndex], heap[index]);
            index = parentIndex;
        }
    }
};
