Sure, here's an implementation of Kruskal's algorithm in C++. This code creates a minimum spanning tree for a given graph using Kruskal's algorithm.

```cpp
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Edge {
    int src, dest, weight;
};

struct DisjointSet {
    int parent, rank;
};

int find(DisjointSet set[], int i) {
    if (set[i].parent != i)
        set[i].parent = find(set, set[i].parent);

    return set[i].parent;
}

void unionSet(DisjointSet set[], int x, int y) {
    int xroot = find(set, x);
    int yroot = find(set, y);

    if (set[xroot].rank < set[yroot].rank)
        set[xroot].parent = yroot;
    else if (set[xroot].rank > set[yroot].rank)
        set[yroot].parent = xroot;
    else {
        set[yroot].parent = xroot;
        set[xroot].rank++;
    }
}

bool compare(Edge a, Edge b) {
    return a.weight < b.weight;
}

void kruskal(vector<Edge> graph[], int V) {
    vector<Edge> mst;
    DisjointSet set[V];

    for (int i = 0; i < V; i++) {
        set[i].parent = i;
        set[i].rank = 1;
    }

    sort(graph->begin(), graph->end(), compare);

    int edge_count = 0;
    while (edge_count < V - 1) {
        Edge next_edge = graph->back();
        graph->pop_back();

        int x = find(set, next_edge.src);
        int y = find(set, next_edge.dest);

        if (x != y) {
            mst.push_back(next_edge);
            edge_count++;
            unionSet(set, x, y);
        }
    }

    cout << "The edges in the minimum spanning tree are:\n";
    for (auto edge : mst) {
        cout << edge.src << " -- " << edge.dest << " == " << edge.weight << "\n";
    }
}

